import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import NormalFormTable from './components/NormalFormTable';
import './App.css';

const tables = [
  { label: 'UNF', endpoints: ['students_unf'], path: 'unf' },
  { label: '1NF', endpoints: ['students_1nf', 'student_subjects_1nf'], path: '1nf' },
  { label: '2NF', endpoints: ['students_2nf', 'addresses', 'branches', 'subjects', 'student_subjects_2nf'], path: '2nf' },
  { label: '3NF', endpoints: ['students_3nf', 'class_teachers'], path: '3nf' },
  { label: 'Pre-BCNF & BCNF', endpoints: ['student_class_teacher_prebcnf', 'students_bcnf'], path: 'bcnf' },
  { label: '4NF', endpoints: ['student_hobbies_languages_pre4nf', 'student_hobbies_4nf', 'student_languages_4nf'], path: '4nf' },
  { label: '5NF', endpoints: ['student_course_instructor_pre5nf', 'student_courses', 'course_instructors', 'student_instructors'], path: '5nf' },
];

function Navbar() {
  return (
    <nav className="navbar">
      {tables.map(({ label, path }) => (
        <Link key={path} to={`/${path}`} className="nav-link">{label}</Link>
      ))}
    </nav>
  );
}

function FormPage({ label, endpoints }) {
  return (
    <section style={{ marginBottom: 40 }}>
      <h2>{label}</h2>
      {endpoints.map((endpoint) => (
        <NormalFormTable key={endpoint} endpoint={endpoint} />
      ))}
    </section>
  );
}

function App() {
  return (
    <Router>
      <div className="container">
        <h1>Database Normalization Forms</h1>
        <Navbar />
        <p style={{ marginTop: 24 }}>This site demonstrates the differences between various forms of normalization using real data from a MySQL database.</p>
        <Routes>
          <Route path="/" element={<Navigate to="/unf" replace />} />
          {tables.map(({ label, endpoints, path }) => (
            <Route key={path} path={`/${path}`} element={<FormPage label={label} endpoints={endpoints} />} />
          ))}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
